// Optimized static file serving with caching headers
app.use('/attached_assets', express.static('attached_assets', {
  maxAge: '1y', // Cache for 1 year
  etag: true,
  lastModified: true
}));

// Serve static files from public directory with optimized caching
app.use(express.static('public', {
  maxAge: '30d', // Cache images for 30 days
  etag: true,
  lastModified: true,
  setHeaders: (res, path) => {
    // Set specific cache headers for different file types
    if (path.match(/\.(jpg|jpeg|png|gif|ico|svg|webp)$/)) {
      res.setHeader('Cache-Control', 'public, max-age=2592000'); // 30 days
    } else if (path.match(/\.(css|js)$/)) {
      res.setHeader('Cache-Control', 'public, max-age=86400'); // 1 day
    }
  }
}));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "‚Ä¶";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  // Initialize database with sample data
  await initializeDatabase();

  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    // Log error but don't throw to prevent crashes
    console.error('Server Error:', err);
    res.status(status).json({ message });
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (process.env.NODE_ENV === 'production') {
    try {
      console.log('Setting up static file serving for production...');
      serveStatic(app);
      console.log('Static file serving setup completed');
    } catch (error) {
      console.error('Error setting up static file serving:', error);
      throw error;
    }
  } else {
    try {
      console.log('Setting up Vite development server...');
      const { setupVite } = await import('./vite');
      await setupVite(app, server);
      console.log('Vite development server setup completed');
    } catch (error) {
      console.error('Error setting up Vite development server:', error);
      throw error;
    }
  }

  // Use the port assigned by Replit or fallback to 5000 for local development
  // this serves both the API and the client.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/notification-service.ts.content:
interface NotificationOptions {
  webhook_url?: string;
  slack_webhook?: string;
  discord_webhook?: string;
  telegram_bot_token?: string;
  telegram_chat_id?: string;
}

// Simple notification system that can be extended
export class NotificationService {
  private static instance: NotificationService;
  private notifications: any[] = [];

  static getInstance() {
    if (!NotificationService.instance) {
      NotificationService.instance = new NotificationService();
    }
    return NotificationService.instance;
  }

  // Store notifications in memory for easy viewing
  addNotification(type: string, data: any) {
    const notification = {
      id: Date.now().toString(),
      type,
      data,
      timestamp: new Date(),
      read: false
    };

    this.notifications.unshift(notification); // Add to beginning

    // Keep only last 100 notifications
    if (this.notifications.length > 100) {
      this.notifications = this.notifications.slice(0, 100);
    }

    return notification;
  }

  getNotifications() {
    return this.notifications;
  }

  getUnreadCount() {
    return this.notifications.filter(n => !n.read).length;
  }

  markAsRead(id: string) {
    const notification = this.notifications.find(n => n.id === id);
    if (notification) {
      notification.read = true;
    }
  }

  markAllAsRead() {
    this.notifications.forEach(n => n.read = true);
  }

  // Optional: Send to webhook if configured
  async sendWebhook(message: string, data: any) {
    const webhookUrl = process.env.WEBHOOK_URL || process.env.SLACK_WEBHOOK_URL;

    if (!webhookUrl) return;

    try {
      const response = await fetch(webhookUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          text: message,
          data: data
        })
      });

      if (!response.ok) {
        console.error('Webhook failed:', response.statusText);
      }
    } catch (error) {
      console.error('Webhook error:', error);
    }
  }
}

export const notificationService = NotificationService.getInstance();

