          });
        } catch (emailError) {
          console.error(
            "Document analysis email notification failed:",
            emailError,
          );
        }
      }

      res.json({
        success: true,
        analysis,
        session: chatSession,
        message: analysisMessage,
      });
    } catch (error) {
