      } catch (emailError) {
        console.error("Failed to send confirmation email:", emailError);
        // Continue even if email fails
      }

      res.status(200).json({ message: "Message received successfully!" });
    } catch (error) {
      console.error("Error processing contact form:", error);
      res
        .status(500)
