export const insertChatSessionSchema = createInsertSchema(chatSessions).pick({
  sessionId: true,
  clientInfo: true,
});

export const insertFeaturedClientSchema = createInsertSchema(
  featuredClients,
).pick({
  servicePage: true,
  name: true,
  logo: true,
  website: true,
  description: true,
  achievements: true,
  industry: true,
  timeframe: true,
  isActive: true,
});

export const insertCaseStudySchema = createInsertSchema(caseStudies).pick({
  servicePage: true,
  title: true,
  client: true,
  industry: true,
  results: true,
  description: true,
  imageUrl: true,
  isActive: true,
