export const insertBlogPostSchema = createInsertSchema(blogPosts, {
  tags: z.array(z.string()).optional(),
}).pick({
  slug: true,
  title: true,
  subtitle: true,
  excerpt: true,
  content: true,
  imageUrl: true,
  tags: true,
  author: true,
  readTime: true,
  isPublished: true,
  isFeatured: true,
  metaDescription: true,
  metaTitle: true,
