              <Button
                variant="outline"
                onClick={() => setShowSettings(false)}
              >
                Cancel
              </Button>
              <Button
                variant="outline"
                onClick={handleRejectAll}
              >
                Reject All
              </Button>
              <Button
                onClick={handleSavePreferences}
                className="bg-brand-coral rand-coral-dark text-white"
              >
                Save Preferences
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </>
  );
}

// Cookie preferences management hook
export function useCookieConsent() {
  const [hasConsent, setHasConsent] = useState(false);
  const [preferences, setPreferences] = useState<CookiePreferences>({
    essential: true,
    analytics: false,
    marketing: false,
    functional: false
  });

  useEffect(() => {
    const consent = localStorage.getItem('cookie-consent');
    if (consent) {
      const prefs = JSON.parse(consent);
      setPreferences(prefs);
      setHasConsent(true);
    }
  }, []);

  const updateConsent = (newPreferences: CookiePreferences) => {
    localStorage.setItem('cookie-consent', JSON.stringify(newPreferences));
    localStorage.setItem('cookie-consent-date', new Date().toISOString());
    setPreferences(newPreferences);
    setHasConsent(true);
  };

  const canUseAnalytics = hasConsent && preferences.analytics;
  const canUseMarketing = hasConsent && preferences.marketing;
  const canUseFunctional = hasConsent && preferences.functional;

  return {
    hasConsent,
    preferences,
    updateConsent,
    canUseAnalytics,
    canUseMarketing,
    canUseFunctional
  };
}

