import React, { useState, useEffect } from 'react';

interface OptimizedImageProps {
  src: string;
  alt: string;
  className?: string;
  fallbackSrc?: string;
  width?: number;
  height?: number;
  loading?: 'lazy' | 'eager';
  disableWebP?: boolean;
  onError?: (error: React.SyntheticEvent<HTMLImageElement>) => void;
  onLoad?: (event: React.SyntheticEvent<HTMLImageElement>) => void;
}

export const OptimizedImage: React.FC<OptimizedImageProps> = ({ 
  src, 
  alt, 
  className = '', 
  fallbackSrc,
  width,
  height,
  loading = 'lazy',
  disableWebP = false,
  onError,
  onLoad 
}) => {
  const [imageState, setImageState] = useState<'loading' | 'loaded' | 'error'>('loading');
  const [currentSrc, setCurrentSrc] = useState<string>('');

  // Normalize image path for consistent handling
  const normalizeImagePath = (imagePath: string): string => {
    // Don't modify external URLs or data URLs
    if (imagePath.startsWith('http') || imagePath.startsWith('data:') || imagePath.startsWith('//')) {
      return imagePath;
    }

    // Handle asset imports (these usually come with query parameters)
    if (imagePath.includes('?')) {
      return imagePath;
    }

    // For absolute paths starting with /, use as-is (these are public folder paths)
    if (imagePath.startsWith('/')) {
      return imagePath;
    }

    // Handle attached_assets references
    if (imagePath.includes('attached_assets/')) {
      const filename = imagePath.split('/').pop();
      return `/images/${filename}`;
    }

    // Handle relative paths from assets folder
    if (imagePath.startsWith('src/assets/') || imagePath.startsWith('assets/')) {
      const filename = imagePath.split('/').pop();
      return `/images/${filename}`;
    }

    // Add /images/ prefix only for relative paths without /
    return `/images/${imagePath}`;
  };

  // Get optimized image source - prefer WebP over original formats unless disabled
  const getOptimizedImageSrc = (originalSrc: string): string[] => {
    const normalizedSrc = normalizeImagePath(originalSrc);
    const sources: string[] = [];

    // If WebP optimization is disabled, use original source only
    if (disableWebP) {
      sources.push(normalizedSrc);
      return sources;
    }

    // If it's already WebP, use it as primary
    if (normalizedSrc.match(/\.webp$/i)) {
      sources.push(normalizedSrc);
      return sources;
    }

    // For other formats, prefer WebP first, then original
    if (normalizedSrc.match(/\.(png|jpg|jpeg)$/i)) {
      const webpSrc = normalizedSrc.replace(/\.(png|jpg|jpeg)$/i, '.webp');
      sources.push(webpSrc); // WebP first
      sources.push(normalizedSrc); // Original as fallback
    } else {
      sources.push(normalizedSrc);
    }

    return sources;
  };

  const [sourcesToTry, setSourcesToTry] = useState<string[]>([]);
  const [currentSourceIndex, setCurrentSourceIndex] = useState(0);

  // Initialize with optimized source list
  useEffect(() => {
    setImageState('loading');
    const optimizedSources = getOptimizedImageSrc(src);
    setSourcesToTry(optimizedSources);
    setCurrentSourceIndex(0);
    setCurrentSrc(optimizedSources[0] || '');
  }, [src]);

  const handleImageError = (e: React.SyntheticEvent<HTMLImageElement>) => {
    console.log('Image load failed for:', currentSrc);

    // Try fallback source first if provided and not already tried
    if (fallbackSrc && !sourcesToTry.includes(normalizeImagePath(fallbackSrc))) {
      console.log('Trying fallback source:', fallbackSrc);
      setCurrentSrc(normalizeImagePath(fallbackSrc));
      return;
    }

    // Try next source in the optimized list
    const nextIndex = currentSourceIndex + 1;
    if (nextIndex < sourcesToTry.length) {
      console.log(`Trying next optimized source (${nextIndex + 1}/${sourcesToTry.length}):`, sourcesToTry[nextIndex]);
      setCurrentSourceIndex(nextIndex);
      setCurrentSrc(sourcesToTry[nextIndex]);
      return;
    }

    // All attempts failed, show error fallback
    console.log('All image load attempts failed, showing error fallback');
    setImageState('error');
    onError?.(e);
  };

  const handleImageLoad = (e: React.SyntheticEvent<HTMLImageElement>) => {
    console.log('Image loaded successfully:', currentSrc);
    setImageState('loaded');
    onLoad?.(e);
  };

  // Show error fallback only when all attempts have failed
  if (imageState === 'error') {
    // Use a generic placeholder or the fallback provided
    const errorFallback = fallbackSrc || '/images/img_g1.png';
    return (
      <div className="relative overflow-hidden">
        <img
          src={errorFallback}
          alt={alt || "Image not available"}
          className={className}
          loading={loading}
          width={width}
          height={height}
          style={{
            width: '100%',
            height: '100%',
            minHeight: height ? `${height}px` : '200px',
            aspectRatio: width && height ? `${width}/${height}` : undefined,
            objectFit: 'cover'
          }}
        />
      </div>
    );
  }

  return (
    <div className="relative overflow-hidden">
      {/* Loading skeleton - only show while loading */}
      {imageState === 'loading' && (
        <div 
          className="absolute inset-0 bg-gray-200 dark:bg-gray-700 animate-pulse rounded flex items-center justify-center z-10"
          style={{ 
            width: '100%',
            height: '100%',
            minHeight: height ? `${height}px` : '200px',
            aspectRatio: width && height ? `${width}/${height}` : undefined
          }}
        >
          <div className="w-8 h-8 border-3 border-gray-300 border-t-brand-purple rounded-full animate-spin"></div>
        </div>
      )}

      {/* Main image */}
      <img
        src={currentSrc}
        alt={alt}
        className={`${className} ${imageState === 'loaded' ? 'opacity-100' : 'opacity-0'} transition-opacity duration-500 ease-in-out`}
        loading={loading}
        width={width}
        height={height}
        onError={handleImageError}
        onLoad={handleImageLoad}
        decoding="async"
        style={{
          maxWidth: '100%',
          height: 'auto',
          objectFit: 'cover'
        }}
      />
    </div>
  );
};

