                          <SelectItem value="ecommerce-store">E-commerce Store</SelectItem>
                          <SelectItem value="full-stack-site">Full Stack Site</SelectItem>
                          <SelectItem value="custom-advanced">Custom Advanced</SelectItem>
                        </SelectContent>
